<script lang="ts">
  import { onMount } from "svelte";
  let more: SVGPathElement;
  let coming: SVGPathElement;
  let soon: SVGPathElement;
  let lol: SVGPathElement;

  const animateSVG = (path: SVGPathElement, seconds: number) => {
    const parent = path.parentNode as HTMLElement;
    const length = path.getTotalLength();
    parent.style.opacity = "1";
    path.style.transition = "none";
    path.style.strokeDasharray = length + " " + length;
    path.style.strokeDashoffset = String(length);
    path.getBoundingClientRect();
    path.style.transition = `stroke-dashoffset ${seconds}s ease-in-out`;
    path.style.strokeDashoffset = "0";
  };

  onMount(() => {
    animateSVG(more, 2.5);

    setTimeout(() => {
      animateSVG(coming, 2.5);
    }, 1800);

    setTimeout(() => {
      animateSVG(soon, 2.5);
    }, 3800);

    setTimeout(() => {
      animateSVG(lol, 2.75);
    }, 5500);
  });
</script>

